mapper.select:
SELECT la."ID_LECCION_APRENDIDA" idLeccionAprendida, la."ID_ENTREGABLE" idEntregableAfectado, (select e."NOMBRE_ENTREGABLE" from "PLANIFICAR_PROYECTO"."ENTREGABLE" e where e."ID_ENTREGABLE" = la."ID_ENTREGABLE") nombreEntregable, la."DESCRIPCION_PROBLEMA" dscproblema, la."CAUSA" dsccausa, la."ACCION_CORRELATIVO" dsccorrelativa, la."RESULTADO_OBTENIDO" dscobtenido, la."LECCION_APREDIDA" dscaprendida, la."ID_PROYECTO" idProyecto, la."COD_LECCION_APRENDIDA" codLeccionAprendida FROM "PLANIFICAR_PROYECTO"."LECCION_APRENDIDA" la
la."ID_LECCION_APRENDIDA" = #{idLeccionAprendida}
AND la."ID_ENTREGABLE" = #{idEntregableAfectado}
AND la."ID_PROYECTO" = #{idProyecto}

SELECT "ID_SPONSOR" id, "DESCRIPCION"
FROM "PLANIFICAR_PROYECTO"."SPONSOR" s
WHERE s."ESTADO" = #{estado};

SELECT ac."ID_ACTA_CIERRE" idActaCierre, ac."ID_SPONSOR" idSponsor, (SELECT "DESCRIPCION" FROM "PLANIFICAR_PROYECTO"."SPONSOR" s WHERE s."ID_SPONSOR" = ac."ID_SPONSOR") dscSponsor, ac."ID_PROYECTO" idProyecto, ac."DECLARACION_ACEPTACION" declAceptacion, ac."OBSERVACIONES" observacion, ac."COMENTARIO" vistaPrevia, ac."FIRMAS" firmas, ac."INCLUIR_ENTREGABLE" tipoLeccionEntregbale, ac."VERSION" versionActa, to_char(ac."FECHA_ELABORACION", 'DD/MM/YYYY') fechaElaboracion, ac."RESPONSABLE" idResponsable, (SELECT p."NOMBRE_PERSONA" ||' '|| p."APELLIDO_PERSONA" FROM seguridad.security_rol_user sru, seguridad.security_user su, "CREAR_PROYECTO"."PERSONA" p WHERE p."ID_PERSONA" = su."ID_PERSONA" AND su.id_user = sru.id_user AND sru.id_detalle = ac."RESPONSABLE") responsable, ac."INCLUIR_ADQUISICIONES" tipoLeccionAdquisiciones, ac."INCLUIR_LECCIONESAPRENDIDAS" tipoLeccionLecAprendida, ac."INCLUIR_ENCUESTA" tipoLeccionEncuesta, ac."ENVIO_CORREO" envioCorreo FROM "PLANIFICAR_PROYECTO"."ACTA_CIERRE" ac
ac."ID_PROYECTO" = #{idProyecto}
AND ac."ID_ACTA_CIERRE" = #{idActaCierre}
