mapper.select:
SELECT "ID_ESTADO_TAREA" id, "DESCRIPCION_ESTADO_TAREA" descripcion, "ESTADO" estado
  FROM "PLANIFICAR_PROYECTO"."ESTADO_TAREA" 
WHERE "ESTADO" = #{estado};

SELECT "ID_COMPLEJIDAD" id, "DESCRIPCION" descripcion, "ESTADO" estado
  FROM "PLANIFICAR_PROYECTO"."COMPLEJIDAD"
WHERE "ESTADO" = #{estado};

SELECT cc."ID_CONTROL_CAMBIOS" idCtrolCambios, cc."DESCRIPCION_SOLICITUD" descripcionCtrolCambios, to_char(cc."FECHA_SOLICITUD", 'DD/MM/YYYY') fecSolCtrolCambios, cc."ID_COMPLEJIDAD" idComplejidad, (SELECT c."DESCRIPCION" FROM "PLANIFICAR_PROYECTO"."COMPLEJIDAD" c WHERE c."ID_COMPLEJIDAD" = cc."ID_COMPLEJIDAD") dscComplejidad, cc."HORAS_ESTIMADAS" horasEstimadas, cc."COMENTARIOS" comentarios, cc."ID_PROYECTO" idproyecto, cc."SOLICITANTE" idsolicitante, cc."SOLICITANTE" dscSolicitante, cc."COD_CONTROL_CAMBIOS" codctrolcambios FROM "PLANIFICAR_PROYECTO"."CONTROL_CAMBIOS" cc
cc."ID_CONTROL_CAMBIOS" = #{idCtrolCambios}
AND cc."ESTADO" = #{estado};
